import React, { useState, useCallback } from 'react';
import '../App.css';
import './CSS/login.css';
import Dashboard from './dashboard';
import Register from './register' ;

function Login({IsSignIn, setIsSignIn}) {
  const [Name, setName] = useState("");
  const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
 

  const EmailValue = (event) => {
  setEmail(event.target.value);
  };

const PasswordValue = (event) => {
  event.preventDefault();
  setPassword(event.target.value);
  };

  const handleIsSignIn = useCallback(event => {
    setIsSignIn(true);
    localStorage.setItem("IsSignIn", true);
  }, [setIsSignIn])

const onSubmitSignIn = (e) => {
  e.preventDefault();
  document.getElementById('logo').style.display="block";
    fetch('https://dcipl.yourtechshow.com/signin', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        email: Email,
        password: Password
      })
    })
      .then(response => response.json())
      .then(user => {
        if (user.id) {
          setName(user.name)
          localStorage.setItem("User", user.name);
          localStorage.setItem("Email", Email);
          handleIsSignIn();
        } else {
          alert("Wrong Credentials")
          document.getElementById('logo').style.display="none";
        }
      })
  }

const OnPageLoad = () => {
const CheckIsSignIn = localStorage.getItem("IsSignIn");
console.log(CheckIsSignIn)
if (CheckIsSignIn) {
  setIsSignIn(true)
} else {
  setIsSignIn(false)
}

}

	return (
    <>
    {IsSignIn ? (
  <>
  <br/>
        <Dashboard User={Name} Email={Email} setUser={setName} setEmail={setEmail}/>
</>
      ) : (
  <main>
            <div className="login-container">
                <div className="login-signin">  
                    <div class="login-title"><h2>Login</h2></div>
                    <form onSubmit={onSubmitSignIn}>
                      <div class="login-input-field"><input type="email" name="email" placeholder="Enter E-Mail ID" required onChange={EmailValue} value={Email}></input></div>
                      <div class="login-input-field"><input type="password" name="password" placeholder="Enter your Password"  required onChange={PasswordValue} value={Password}></input></div>
                    </form>
                    <div className= "login-btn-group">
                      <button className="login-btn" type="submit"><a href="Dashboard">Login</a></button>
                      <button className="login-btn" type="submit">Register</button>
                    </div>
                </div>
            </div>    
                {OnPageLoad()}
                <div id="logo" class="loadingio-spinner-rolling-kswyn6f3gj7"><div class="ldio-c9p079igqka"></div></div>
        </main>
        )}
		</>

		)
};

export default Login;
