import React from 'react';
import logo from '../assests/log.png';
import '../App.css';

import { Link } from "react-router-dom";

function LogoutNow() {
  alert("You have Successfully Logged Out. Hope to see you again.")
  localStorage.removeItem("IsSignIn");
  localStorage.removeItem("User");
  localStorage.removeItem("Email");
  window.location.reload();
}

function Nav({IsSignIn, setIsSignIn}) {
  
	return (
<>
    {IsSignIn ? (

  <>
  <header className="header">
  <a href="./"><img src={logo} className="logo" alt=""></img></a>
  <input className="menu-btn" type="checkbox" id="menu-btn" />
  <label className="menu-icon" htmlFor="menu-btn"><span className="navicon"></span></label>
		
  <ul className="menu">
  <li><a href="../">Home</a></li>
    <li><a href="../about">About</a></li>
    <li><a href="../blog">Blog</a></li>
    <li><a href="../careers">Careers</a></li>
    <li><button onClick={LogoutNow} >Logout</button></li>


  </ul>
</header>



</>
      ) : (

		<>
		<header className="header">
  <a href="./"><img src={logo} className="logo" alt=""></img></a>
  <input className="menu-btn" type="checkbox" id="menu-btn" />
  <label className="menu-icon" htmlFor="menu-btn"><span className="navicon"></span></label>
  <ul className="menu">
  <li><a href="/Home">Home</a></li>
    <li><a href="/about">About</a></li>
    <li><a href="./blog">Blog</a></li>
    <li><a href="./careers">Careers</a></li>
    <li><a href="./login">Login</a></li>
    <li><a href="./register">Register</a></li>
  </ul>
</header>
<div class="blog-progress" style="will-change: transform; transform: translate3d(0px, 0px, 0px) scale3d(0, 1, 1) rotateX(0deg) rotateY(0deg) rotateZ(0deg) skew(0deg, 0deg); transform-style: preserve-3d;"></div>
</>
     )}
</>
		)
};

export default Nav;
