import React from 'react';
import logo from '../assests/logo.jpeg';
import '../App.css';

import { Link } from "react-router-dom";

function LogoutNow() {
  alert("You have Successfully Logged Out. Hope to see you again.")
  localStorage.removeItem("IsSignIn");
  window.location.reload();
}

function Nav({IsSignIn, setIsSignIn}) {
  
	return (
<>
    {IsSignIn ? (

  <>
  <header className="header">
  <a href="./"><img src={logo} className="logo" alt=""></img></a>
  <input className="menu-btn" type="checkbox" id="menu-btn" />
  <label className="menu-icon" htmlFor="menu-btn"><span className="navicon"></span></label>
		<div className="menu">
  <ul>
  <li><a href="../">Home</a></li>
    <li><a href="../about">About</a></li>
    <li><a href="../careers">Careers</a></li>
    <li><button onClick={LogoutNow} >Logout</button></li>

  </ul>
</div>

</header>
</>
      ) : (

		<>
		<header className="header">
    <nav>
  <a href="./"><img src={logo} className="logo" alt=""></img></a>
  <label className="menu-icon" htmlFor="menu-btn"><span className="navicon"></span></label>
  <div className="menu">
  <ul >
  <li><a href="/">Home</a></li>
    <li><a href="/about">About</a></li>
    <li><a href="./careers">Careers</a></li>
   
    <li className="login-btn"><a href="./login">Login/Register</a></li>
    
   
  </ul>
  
  </div>
  </nav>
</header>
</>
     )}
</>
		)
};

export default Nav;
