import React, { useState } from 'react';
import '../App.css';
import Dashboard from './dashboard';


function Register() {

  const [User, setUser] = useState();
  const [IsSignIn, setIsSignIn] = useState(false);

  const NameValue = (event) => {
  event.preventDefault();
  setUser(event.target.value);
  };

  const OnSubmit = (event) => {
    event.preventDefault();
    this.IsSignIn=true;
  }
	return (
		<>
{IsSignIn ? (
  <>
  <br/>
        <Dashboard User={setUser}/>
</>
      ) : (
        <>
 <br/><br/><br/>
  <htmlform action={OnSubmit}>
  <div className="container">
    <h1>Sign Up</h1>
    <p>Please fill in this form to create an account.</p>
  <br/ >

    <label htmlFor="email"><b>Name</b></label>
    <input type="text" placeholder="Enter Name" name="email" required onChange={NameValue} value={User}/>

    <label htmlFor="psw"><b>Password</b></label>
    <input type="password" placeholder="Enter Password" name="psw" required />

    <label htmlFor="psw-repeat"><b>Repeat Password</b></label>
    <input type="password" placeholder="Repeat Password" name="psw-repeat" required />

    <label>
      <input type="checkbox" checked="checked" name="remember" /> Remember me
    </label>

    <p>By creating an account you agree to our <a href="#">Terms & Privacy</a>.</p>

    <div className="clearfix">
      <button type="button" className="cancelbtn">Cancel</button>
      <button type="submit" className="signupbtn" OnClick={OnSubmit} >Sign Up</button>
    </div>
  </div></htmlform>
</>
      )}



		</>
		)
};

export default Register;
