      allocatedWeight4: AllocatedWeight4,
      return4: Return4,
      SD4: SD4,
      crypto: Crypto,
      allocatedWeight5: AllocatedWeight5,
      return5: Return5,
      SD5: SD5,
      forex: Forex,
      allocatedWeight6: AllocatedWeight6,
      return6: Return6,
      SD6: SD6
