import React, { useState , useEffect }  from 'react';
import './CSS/admin.css';
import Login from './login';

function Admin() {
const [IsSignIn, setIsSignIn] = useState(false);
const [User, setUser] = useState("");
const [Email, setEmail] = useState("");
var [date,setDate] = useState(new Date());
const OnPageLoad = () => {
fetch('http://localhost:8000/users')
      .then(response => response.json())
      .then(response => {
      	console.log(response)
      	var txt = ""
      	var i =""
      	for (i = 0; i < response.length; i++) {
      	txt += "<table>"
      	txt += "<tr>"
  		txt += "<td>" + JSON.stringify(response[i].id) + "</td>";
  		txt += "<td>" + JSON.stringify(response[i].name) + "</td>";
  		txt += "<td>" + JSON.stringify(response[i].email) + "</td>";
  		txt += "<td>" + JSON.stringify(response[i].joined) + "</td>";
    	txt += "</tr>"
    	txt += "</table>"
}

      document.getElementById('users').innerHTML=txt;
      })
}   
    useEffect(() => {
        var timer = setInterval(()=>setDate(new Date()), 1000 )
        return function cleanup() {
            clearInterval(timer)
        }

    });

	return (
       <>
    {IsSignIn ? (
  <>
  <div>
<br/>
<p>Welcome, Admin Today is {date.toLocaleString()}</p>
<table>
<tr>
<th>ID</th>
<th>Name</th>
<th>Email</th>
<th>Joined</th>
</tr>
<div id="users">
</div>
</table>


{OnPageLoad()}
</div>
</>
      ) : (
<div>
<Login IsSignIn={IsSignIn} setIsSignIn={setIsSignIn}/>
</div>
        )}
		</>

)
};

export default Admin;
