import React, { useState } from "react";
import "./CSS/Home.css";
import { useHistory } from "react-router-dom";
import derivebox from "../assests/deriveup.svg";
import image28 from "../assests/tax1.png";
import image29 from "../assests/cash1.png";
import image31 from "../assests/bank111.png";
import image33 from "../assests/hm1.png";
import image27 from "../assests/planning1.png";
import gplay from "../assests/g-play.png";
import image4 from "../assests/grid-1.svg";
import { Link } from "react-router-dom";
import image10 from "../assests/grid-5.svg";
import image26 from "../assests/budget1.png";
import tripv from "../assests/tripv.mp4";
import image6 from "../assests/grid-2.svg";
import image12 from "../assests/grid-6.svg";
import logo from "../assests/logo.jpg";
import Machine from "../assests/big-img.svg";
import card from "../assests/card.png";
import image8 from "../assests/grid-3.svg";
import piggy from "../assests/piggy.png";
import atm from "../assests/atm.png";
import mobile from "../assests/mobile.png";
import AppointmentModal from "../Pages/modal/AppointmentModal";
// import Postmodal from "../Pages/modal/Postmodal";s

// Progress Bar
window.addEventListener(
  "scroll",
  () => {
    document.body.style.setProperty(
      "--scroll",
      window.pageYOffset / (document.body.offsetHeight - window.innerHeight)
    );
    document.body.style.setProperty(
      "--scroll-per",
      `${(window.pageYOffset / document.body.offsetHeight) * 110}%`
    );
  },
  false
);

const Box_component = ({ Icon, Text }) => {
  const [isShown, setIsShown] = useState(false);

  let history = useHistory();

  function handleClick() {
    history.push("/register");
  }

  return (
    <div
      onMouseEnter={() => setIsShown(true)}
      onMouseLeave={() => setIsShown(false)}
      onClick={handleClick}
      className="box33"
    >
      <img src={Icon} className="icon_design" alt="" />
      <h5 className="box_text">{Text}</h5>
      {isShown && (
        <div className="box-inner">
          <h5 className="box_text2">{Text}</h5>
          <button className="btnviewdoc">View Solutions</button>
        </div>
      )}
    </div>
  );
};

//BBC hompage slider
var homeSlider = {
  vars: {
    //tray position
    pos: 0,
    //lock the slider to prevent multiple clicks when animating
    locked: 0,
    //animation duration
    duration: 1000,
  },

  helpers: {
    //return percent from .att('style')
    getPercent: function (str) {
      var foo = str.substring(str.indexOf("left:") + 5);
      return parseInt(foo.substring(0, foo.indexOf("%")));
    },

    //unlock the slider post animation
    unlock: function () {
      homeSlider.vars.locked = 0;
    },
  },

  //bind all the things
  init: function () {
    //click binds
    $(".slider .mask").on("mouseenter mouseleave", homeSlider.peek.over);
    $(".slider .mask").on("click", homeSlider.nav.arrow);
    $(".slider-nav a").on("click", homeSlider.nav.menu);

    //mobile support
    /*
		$('.slider .outer').swipe({
			swipe: function(event, direction, distance, duration, fingerCount){
				if ( direction === 'left' ){
					$('.mask.right').click();
				} else if ( direction === 'right' ){
					$('.mask.left').click();
				}
			},
			threshold: 40
		});
		*/
  },

  peek: {
    //hover on masks triggers peek direction
    over: function (e) {
      if (e.type == "mouseenter" && $(this).hasClass("left")) {
        homeSlider.peek.move(15);
      } else if (e.type == "mouseenter" && $(this).hasClass("right")) {
        homeSlider.peek.move(-15);
      } else {
        homeSlider.peek.move(0);
      }
    },

    //peek move
    move: function (dist) {
      $(".slider-wrap .outer").stop().animate({ left: dist }, 300);
    },
  },

  nav: {
    //handles arror nav click
    arrow: function (e) {
      e.preventDefault();
      if (homeSlider.vars.locked === 0) {
        homeSlider.vars.locked = 1;
        if ($(this).hasClass("left")) {
          homeSlider.move.direction(0);
        } else {
          homeSlider.move.direction(1);
        }
      }
    },

    //handles a menu nav click
    menu: function (e) {
      e.preventDefault();
      if (homeSlider.vars.locked === 0) {
        homeSlider.vars.locked = 1;
        homeSlider.move.toSlide($(e.target.hash));
      }
    },

    //updates the menu nav visual class and arrow text
    update: function () {
      //nav menu
      homeSlider.move.currentClass(
        $(".slider-nav")
          .find('a[href="#' + $(".slider .current").attr("id") + '"]')
          .parent()
      );
      var offset =
        ($(".slider-nav li.current").position().left /
          $(".slider-nav").width()) *
        100;
      $(".slider-nav .highlight")
        .stop()
        .animate(
          { left: offset + "%" },
          homeSlider.vars.duration,
          "easeInOutQuart"
        );
      //update arrow text
      $(".arrow.right span").text(
        $(".slider-nav")
          .find('a[href="#' + $(".slider .current").next().attr("id") + '"]')
          .text()
      );
      $(".arrow.left span").text(
        $(".slider-nav")
          .find('a[href="#' + $(".slider .current").prev().attr("id") + '"]')
          .text()
      );
    },
  },

  move: {
    direction: function (dir) {
      var $curr = $(".slider .current");

      //go right
      if (dir === 1) {
        homeSlider.vars.pos -= 100;
        if ($curr.next().is(":last-child")) {
          //append first to last
          homeSlider.move.firstToLast();
          homeSlider.move.animate(homeSlider.vars.pos, true);
        } else {
          //no appendingzings
          homeSlider.move.animate(homeSlider.vars.pos, false);
        }
        homeSlider.move.currentClass($curr.next());

        //go left
      } else {
        homeSlider.vars.pos += 100;
        if ($curr.prev().is(":first-child")) {
          //prepend last to first
          homeSlider.move.lastToFirst();
          homeSlider.move.animate(homeSlider.vars.pos, true);
        } else {
          //no appendingzings
          homeSlider.move.animate(homeSlider.vars.pos, false);
        }
        homeSlider.move.currentClass($curr.prev());
      }

      //if the slider is currently peeking, remove peek
      homeSlider.peek.move(0);
      //update the bottom nav
      homeSlider.nav.update();
    },

    toSlide: function ($elm) {
      var pos = homeSlider.helpers.getPercent($elm.attr("style")) * -1;

      //if moving to the beginning of the stack
      if ($elm.is(":first-child")) {
        homeSlider.move.lastToFirst();
        homeSlider.move.currentClass($elm);
        homeSlider.move.animate(pos, true);

        //if moving to the end of the stack
      } else if ($elm.is(":last-child")) {
        homeSlider.move.firstToLast();
        homeSlider.move.currentClass($elm);
        homeSlider.move.animate(pos, true);

        //if moving to the middle of the stack
      } else {
        homeSlider.move.currentClass($elm);
        homeSlider.move.animate(pos, false);
      }

      //update the bottom nav
      homeSlider.nav.update();
      //update the tray positon
      homeSlider.vars.pos = pos;
    },

    //updates current class to requested slide
    currentClass: function ($elm) {
      $elm.addClass("current").siblings().removeClass("current");
    },

    //animation of the slider tray
    animate: function (pos, flush) {
      $(".slider .tray").animate(
        { left: pos + "%" },
        homeSlider.vars.duration,
        "easeInOutQuart",
        function () {
          if (flush) {
            homeSlider.move.flush();
          } else {
            homeSlider.helpers.unlock();
          }
        }
      );
    },

    //dupe first slide to end of stack + mark first slide for removal
    firstToLast: function () {
      var $elm = $(".slider section:first-child");
      pos =
        homeSlider.helpers.getPercent(
          $(".slider section:last-child").attr("style")
        ) + 100;
      $clone = $elm.clone();
      $elm.addClass("remove");
      $clone
        .removeAttr("style")
        .css({ left: pos + "%" })
        .appendTo($(".slider .tray"));
    },

    //dupe last slide to beginning of stack + mark last slide for removal
    lastToFirst: function () {
      var $elm = $(".slider section:last-child");
      pos =
        homeSlider.helpers.getPercent(
          $(".slider section:first-child").attr("style")
        ) - 100;
      $clone = $elm.clone();
      $elm.addClass("remove");
      $clone
        .removeAttr("style")
        .css({ left: pos + "%" })
        .prependTo($(".slider .tray"));
    },

    //remove old slide from stack
    flush: function () {
      $(".slider .tray section.remove").remove();
      homeSlider.helpers.unlock();
    },
  },
};

var helper = {
  init: function () {
    helper.center();
    $(window).resize(helper.center);
  },

  center: function () {
    $.each($(".slider section img"), function (i, elm) {
      $(elm).css({
        marginTop: -$(elm).height() / 2,
        top: "50%",
        position: "relative",
      });
    });
  },
};

homeSlider.init();
helper.init();
const Home = () => {
  const [postIsOpen, setPostIsOpen] = useState(false);
  const [appointmentIsOpen, setAppointmentIsOpen] = useState(false);
  return (
    <>
      <div className="progress"></div>
      <div className="overall">
        <section className="first-box">
          <div className="first-box-left">
            <h6> DERIVE CAPITAL</h6>
            <h3>
              The <span>smart </span>
              next gen <span> money app</span>
            </h3>
            <img src={derivebox} alt="#"></img>
            <div className="s-box">
              <h4>
                <h1>#DERIVEUP </h1> to begin saving 3X more than others!
              </h4>
            </div>{" "}
            <img src={gplay} alt="#"></img>
            {/* <h5>to begin saving 3X more than others!</h5> */}
            <div class="mobile-input p-1">
              <label for="userMobile" class="sr-only">
                Enter your mobile number
              </label>{" "}
              <input
                id="userMobile"
                autofocus="autofocus"
                type="tel"
                name="user-mobile"
                placeholder="Enter your mobile number"
              />{" "}
              <button id="join-btn-top" class="btn theme-btn fo-sm-16">
                Get App Link
              </button>
            </div>
          </div>
          {/* <div className="g-play">
          <a href="">
            {" "}
            <img src={gplay} alt="#"></img>
          </a>
        </div>{" "} */}
          <div className="first-box-right">
            <div class="wrapper-main1">
              <div class="carousel">
                <div class="carousel__item">
                  <div class="carousel__item-head">🚀</div>
                  <div class="carousel__item-body">
                    <p class="title-slide">Save money to buy the future.</p>
                    <p>Derive Capital</p>
                  </div>
                </div>
                <div class="carousel__item">
                  <div class="carousel__item-head">🏠</div>
                  <div class="carousel__item-body">
                    <p class="title-slide">Start earning more today.</p>
                    <p>Derive Capital</p>
                  </div>
                </div>
                <div class="carousel__item">
                  <div class="carousel__item-head">💴</div>
                  <div class="carousel__item-body">
                    <p class="title-slide">A better financial approach.</p>
                    <p>Derive Capital</p>
                  </div>
                </div>
                <div class="carousel__item">
                  <div class="carousel__item-head">💸</div>
                  <div class="carousel__item-body">
                    <p class="title-slide">
                      Towards a better financial future.
                    </p>
                    <p>Derive Capital</p>
                  </div>
                </div>
                <div class="carousel__item">
                  <div class="carousel__item-head">💹</div>
                  <div class="carousel__item-body">
                    <p class="title-slide">Where your money lives and grows.</p>
                    <p>Derive Capital</p>
                  </div>
                </div>
                <div class="carousel__item">
                  <div class="carousel__item-head">💱</div>
                  <div class="carousel__item-body">
                    <p class="title-slide">Five-star service guaranteed.</p>
                    <p>Derive Capital</p>
                  </div>
                </div>
                <div class="carousel__item">
                  <div class="carousel__item-head">💶</div>
                  <div class="carousel__item-body">
                    <p class="title-slide">Quality in everything we do.</p>
                    <p>Derive Capital</p>
                  </div>
                </div>
                <div class="carousel__item">
                  <div class="carousel__item-head">💰</div>
                  <div class="carousel__item-body">
                    <p class="title-slide">We know money.</p>
                    <p>Derive Capital</p>
                  </div>
                </div>
                <div class="carousel__item">
                  <div class="carousel__item-head">💷</div>
                  <div class="carousel__item-body">
                    <p class="title">Start earning more today.</p>
                    <p>Derive Capital</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
        {/* <Link to="" className="float56" onClick={() => setPostIsOpen(true)}>
        <i className="fa fa-info"></i>
      </Link>
      <Postmodal
        postIsOpen={postIsOpen}
        setPostIsOpen={setPostIsOpen}
      ></Postmodal> */}
        <div className="social-icons">
          <a className="s-icons" href="#">
            <i className="fab fa-facebook-f"></i>
          </a>
          <a className="s-icons" href="#">
            <i className="fab fa-linkedin-in"></i>
          </a>
          <a className="s-icons" href="#">
            <i className="fab fa-instagram"></i>
          </a>
          <a className="s-icons" href="#">
            <i className="fab fa-twitter"></i>
          </a>
          <a className="s-icons" href="#">
            <i className="fab fa-youtube"></i>
          </a>
        </div>
        {/* page-2 */}
        <section className="second-box">
          <div className="second-box-left">
            <img className="grid-1" src={image4} alt="#"></img>
            {/* <div className="box-13"></div> */}
