              <div className="row1">
                <Box_component Icon={image33} Text={"Retirement Planning"} />
                <Box_component Icon={image31} Text={"Estate Planning"} />
                <Box_component Icon={image27} Text={"Mutual Fund"} />
              </div>
            </div>
          </div>
        </section>{" "}
        <section className="second-box">
          {" "}
          <div className="second-box-left">
