import React, { useState } from 'react';
import '../App.css';
import dashboard from './dashboard';


class Register extends React.Component {



render() {
  const [User, setUser] = useState();

  const NameValue = (event) => {
  event.preventDefault();
  setUser(event.target.value);
  };

  const OnSubmit = (event, User) => {
    event.preventDefault();
    <dashboard User="User" />
  }

	return (
		<>

		<br/ ><br/ ><br/ >
	<form>
  <div class="container">
    <h1>Sign Up</h1>
    <p>Please fill in this form to create an account.</p>
  <br/ >
<dashboard User="Akshay"/>
    <label for="email"><b>Name</b></label>
    <input type="text" placeholder="Enter Name" name="email" required onChange={NameValue} value={User}/>

    <label for="psw"><b>Password</b></label>
    <input type="password" placeholder="Enter Password" name="psw" required />

    <label for="psw-repeat"><b>Repeat Password</b></label>
    <input type="password" placeholder="Repeat Password" name="psw-repeat" required />

    <label>
      <input type="checkbox" checked="checked" name="remember" /> Remember me
    </label>

    <p>By creating an account you agree to our <a href="#">Terms & Privacy</a>.</p>

    <div class="clearfix">
      <button type="button" class="cancelbtn">Cancel</button>
      <button  class="signupbtn" onClick={OnSubmit}>Sign Up</button>
    </div>
  </div></form>
		</>
		)}
};

export default Register;
