                <div className="logregister_form">  
                    <h2>Login/Register</h2>
                    <form onSubmit={onSubmitSignIn}>
                        <br></br>
                        <input type="email" name="email" placeholder="Enter E-Mail ID" required onChange={EmailValue} value={Email}></input><br></br>
